function swapAdjectives(sentence) {
  // Split the sentence into an array of words
  const words = sentence.split(" ");

  // Find the index of the first adjective (excluding articles like "a" and "an")
  let firstAdjIndex = -1;
  for (let I = 0; I < words.length; i++) {
    const word = words[i].trim();
    if (word.match(/^[a-z]+\b/g)) {
      const isAdj = word.match(/e$/) || word.match(/ly$/) || word.match(/er$/) || word.match(/est$/);
      if (!isAdj && firstAdjIndex === -1) {
        firstAdjIndex = i;
      }
    }
  }

  // Swap the positions of the first and second adjectives
  if (firstAdjIndex !== -1 && firstAdjIndex > 0) {
    [words[firstAdjIndex], words[firstAdjIndex - 1]] = [words[firstAdjIndex - 1], words[firstAdjIndex]];
  }

  // Join the words back into a sentence
  return words.join(" ");
}

// Example usage
