```javascript
function swapAdjectives(sentence) {
  // Split the sentence into an array of words
  const words = sentence.split(" ");

  // Define a regular expression to match adjectives
  const adjectiveRegex = /\b[a-zA-Z]{1,}[a-e]ly\b|\b[a-zA-Z]{2,}\b/;

  // Find the indices of the first two adjectives
  let i = 0;
  let j = 0;
  while (true) {
    const index = words.indexOf(words[i], j);
    if (index === -1) break;
    j = index + 1;
    if (j >= words.length || !adjectiveRegex.test(words[j])) continue;
    j++;
    break;
  }

  // Swap the positions of the first two adjectives
  [words[i], words[j]] = [words[j], words[i]];

  // Join the words back into a sentence
  return words.join(" ");
}

// Example usage
const originalSentence = "Beautiful green trees are in the park";
const newSentence = swapAdjectives(originalSentence);
console.log(newSentence); // Output: "Green beautiful trees are in the park"
```

