enum Homophone: String {
    case their = "they're" // they are/they have
    case there = "there" // in that place
    case theyre = "there" // in that place
    case weather = "whether" // whether (condition)
    case weigh = "way" // direction
    case weigh2 = "weigh" // measurement
    case sea = "see" // see
    case knowledge = "knowledge" // knowledge
    case seem = "seem" // appear
    case seem2 = "seam" // seam (joining of fabric)
    case good = "good" // excellent
    case too = "to" // until
    case right = "write" // write
    case write = "right" // correct
}

func replaceHomophones(in sentence: String) -> String {
    let words = sentence.split(separator: .whitespacesAndNewlines)
    var result = ""

    for word in words {
        let parts = word.split(separator: " ")

        var newParts = [String]()
        for part in parts {
            for (index, homophone) in Homophone.allCases.enumerated() {
                if part == homophone.rawValue {
                    newParts.append(homophone.rawValue)
                    newParts.append(contentsOf: Array(part[index..<part.index(part.startIndex, offsetBy: homophone.rawValue.utf16.count)]))
                    break
                }
            }

            newParts.append(contentsOf: Array(part[(part.index(part.startIndex, offsetBy: homophone.rawValue.utf16.count)..<part.endIndex].dropFirst()))
        }

        result += newParts.joined(separator: " ")
        result += " "
    }

    return String(result.prefix(upTo: result.index($0.characters.last, offsetBy: -1, limitedBy: result.endIndex)))
}

