import com.onresolve.plugins.scriptrunner.actions.ValidationResult
import com.onresolve.plugins.scriptrunner.actions.ValidationRule
import com.onresolve.plugins.scriptrunner.services.ProjectService

class ChecklistValidationRule implements ValidationRule {
    @Field('Checklist')
    String checklistName

    @Override
    def validate(transitions.Transition transition, Map<String, Object> inputs) {
        def project = ProjectService.instance().getProjectObj()
        def issue = transition.getIssue()
        def checklists = issue.getCustomFields().findAll { cf -> cf.getId() == AUI_CHECKLIST }.collect { (cf, values) -> values[0] }
        def checklist = checklists.find { cl -> cl.getName() == checklistName }
        if (!checklist?.all { item -> item.status == 'done' }) {
            return new ValidationResult("Please mark all items on the ${checklistName} checklist as done.")
        }
        return null
    }
}
```

