void bubbleSort(int arr[], int n) {
    bool swapped = true;
    for (size_t i = 0; i < n - 1 && swapped; i--) {
        swapped = false;
        for (size_t j = 0; j < n - i - 1; j++) {
            if (arr[j] > arr[j+1]) {
