function reorderAdjectives(sentence) {
  // Split the sentence into an array of words
  const words = sentence.split(' ');

  // Filter out common stop words (you can customize this list)
  const stopWords = ['a', 'an', 'the'];
  const filteredWords = words.filter((word) => !stopWords.includes(word.toLowerCase()));

  // Find all adjectives in the filtered words
  const adjectives = filteredWords.filter((word) => /\b[a-zA-Z]{3,}\b/.test(word));

  // Reverse the order of adjectives and concatenate them back to the original sentence
  const reversedAdjectives = adjectives.reverse().join(' ');
  const updatedSentence = filteredWords.slice(0, filteredWords.indexOf(adjectives[0])).concat(reversedAdjectives, filteredWords.slice(filteredWords.indexOf(adjectives[0])+1)).join(' ');

  return updatedSentence;
}

