    def validate(transitionalContext) {
        def project = ProjectService.instance.getProject(transitionalContext.project)
        def issue = transitionalContext.issue
        def checklistItems = project.content.findAll { it instanceof com.atlassian.confluence.content.checksummary.CheckItem && it.name == checklistName }.collect { it.content as Map }
