import React, { useState } from 'react';
import './App.css';

function App() {
  const [temperature, setTemperature] = useState(0);
  const [unit, setUnit] = useState('celsius');

  // Conversion functions for Celsius and Fahrenheit
  const celsiusToFahrenheit = (t) => {
    return Math.round((t * 9 / 5) + 32);
  };

  const fahrenheitToCelsius = (t) => {
    return Math.round((t - 32) * 5 / 9);
  };

  // Handler for input field change
  const handleInputChange = (e) => {
    const value = parseFloat(e.target.value);
    const newUnit = unit === 'celsius' ? 'fahrenheit' : 'celsius';
    setTemperature(value);
    setUnit(newUnit);
  };

  // Handle conversion based on current unit
  const handleConversion = () => {
    if (unit === 'celsius') {
      const convertedTemp = celsiusToFahrenheit(temperature);
      setTemperature(convertedTemp);
      setUnit('fahrenheit');
    } else {
      const convertedTemp = fahrenheitToCelsius(temperature);
      setTemperature(convertedTemp);
      setUnit('celsius');
    }
  };

  // Render the component
  return (
    <div className="App">
      <h1>Temperature Converter</h1>
      <label htmlFor="input-field">Enter Temperature:</label>
      <input type="number" id="input-field" onChange={handleInputChange} />
      <button onClick={handleConversion}>Convert</button>
      <p>{Math.round(temperature)} {unit === 'celsius' ? '°C' : '°F'}</p>
    </div>
  );
}

