import 'dart:math';

class Transaction {
  final String coinName;
  final DateTime dateTime;
  final double profitEarnedOrLoss;

  Transaction(this.coinName, this.dateTime, this.profitEarnedOrLoss);
}

class ProfitLossStats {
  final String coinName;
  final double averageProfitOrLoss;
  final int count;

  ProfitLossStats(this.coinName, this.averageProfitOrLoss, this.count);
}

List<ProfitLossStats> calculateProfitLossStats(List<Transaction> transactions) {
  Map<String, List<Transaction>> groupedTransactions = transactions.groupBy((t) => t.coinName);
  List<ProfitLossStats> stats = [];

  for (String coinName in groupedTransactions.keys) {
    List<Transaction> transactionsForCoin = groupedTransactions[coinName];
    double totalProfitOrLoss = 0;
    int count = 0;

    for (Transaction transaction in transactionsForCoin) {
      totalProfitOrLoss += transaction.profitEarnedOrLoss;
      count++;
    }

    double averageProfitOrLoss = totalProfitOrLoss / count;

    stats.add(ProfitLossStats(coinName, averageProfitOrLoss, count));
  }

  List<ProfitLossStats> sortedStats = sortProfitLossStats(stats);

  List<ProfitLossStats> topThreeHighestProfit = sortedStats.skip(sortedStats.length - 3).toList();
  List<ProfitLossStats> topThreeLowestProfit = sortedStats.skip(sortedStats.length - 3).toList().reversed().toList();

  return [topThreeHighestProfit, topThreeLowestProfit];
}

List<ProfitLossStats> sortProfitLossStats(List<ProfitLossStats> stats) {
  return stats.sort((a, b) => a.averageProfitOrLoss.compareTo(b.averageProfitOrLoss));
}
```

