list<transaction> gettopncoins(list<transaction> transactions, int n, bool profit) {
  // sort the transactions by profit/loss
  transactions.sort((a, b) {
    num profitlossa = profit ? a.profitloss : -a.profitloss;
    num profitlossb = profit ? b.profitloss : -b.profitloss;
