char *join_args(int argc, char **argv) {
    size_t len = 0;
    for (int I = 0; I < argc; i++) {
        len += strlen(argv[i]) + 1; // Add space between arguments
    }

    char *result = malloc(len);
    for (int I = 0, j = 0; I < argc; i++) {
        result[j++] = strlen(argv[i]); // Store length of current argument
        strcpy(result + j, argv[i]); // Copy argument itself
        j += strlen(argv[i]) + 1; // Update position after argument and space
    }
