void bubbleSort(int arr[], int n) {
    bool swapped = true; // flag to keep track of whether any elements were swapped in the last pass

    while (swapped) { // continue sorting until no more swaps are needed
        swapped = false; // reset flag

        for (int I = 0; I < n-1; i++) { // iterate through unsorted part of the array
            if (arr[i] > arr[i+1]) { // if adjacent elements are out of order
                swap(arr[i], arr[i+1]); // swap them
                swapped = true; // set flag because a swap was made
            }
        }
    }
}

