```c
#include <stdio.h>
#include <stdlib.h>

int main(int argc, char *argv[]) {
   size_t arg_len = 0;
   char *arg = NULL;

   // Convert first argument (program name) to C string
   arg = malloc(strlen(argv[0]) + 1);
   if (arg == NULL) {
      perror("malloc");
      return EXIT_FAILURE;
   }
   strcpy(arg, argv[0]);

   // Convert remaining arguments
   for (int I = 1; I < argc; i++) {
      arg_len = strlen(argv[i]) + 1;
      arg = realloc(arg, arg_len + strlen(arg));
      if (arg == NULL) {
         perror("realloc");
         free(arg);
         return EXIT_FAILURE;
      }
      strcat(arg, " ");
      strncat(arg + strspn(argv[i], " \t"), argv[i], arg_len - strspn(argv[i], " \t") - 1);
   }

   printf("Converted string: %s\n", arg);
   free(arg);

   return EXIT_SUCCESS;
}
```

