char *join_args(int argc, char **argv) {
    size_t len = 0;
    for (int I = 0; I < argc; i++) {
        len += strlen(argv[i]) + 1; // Add space between arguments
    }

    char *result = malloc(len);
    for (int I = 0, j = 0; I < argc; i++) {
        result[j++] = *(argv[i]); // Copy first character
        while (*(++argv[i])) { // Copy remaining characters, skipping spaces
            result[j++] = *(argv[i]);
        }
        if (i != argc - 1) { // Add space between arguments, except last one
