import com.onemonks.scriptapi.validation.ValidationResult
import com.onemonks.scriptapi.validation.ValidationRule

class ChecklistCompleteValidationRule implements ValidationRule {
    @Field('checklistName')
    String checklistName = ''

    def validate(context) {
