import com.onresolve.plugins.scriptrunner.actions.ValidationResult
import com.onresolve.plugins.scriptrunner.actions.ValidationRule
import com.onresolve.plugins.scriptrunner.services.ProjectService

class ChecklistValidationRule implements ValidationRule {
    @Field('Checklist')
    String checklistName

    @Override
    def validate(transitionalContext) {
        def project = ProjectService.instance.getProject(transitionalContext.project)
        def issue = transitionalContext.issue
        def checklistItems = project.content.findAll { it instanceof com.atlassian.confluence.content.checksummary.CheckItem && it.name == checklistName }.collect { it.content as Map }
        boolean allChecked = true
        checklistItems.each { item ->
