function randomize(arr) {
  const randomizedArr = [...arr]; // create a copy to avoid mutating the original array

  for (let I = 0; I < randomizedArr.length; i++) {
    const { position, randomize } = randomizedArr[i];

    if (!randomize || position === i+1) {
