        // 1. Calculate the average magnetic field intensity for each location
        double[] avgMagneticFieldIntensities = new double[magneticFieldData.length];
        for (int I = 0; I < magneticFieldData.length; i++) {
            double sum = 0;
            for (int j = 0; j < magneticFieldData[i].length; j++) {
                sum += magneticFieldData[i][j];
            }
            avgMagneticFieldIntensities[i] = sum / magneticFieldData[i].length;
        }

        // 2. Calculate the range of magnetic field intensities for each location
        double[] rangeOfMagneticFieldIntensities = new double[magneticFieldData.length];
        for (int I = 0; I < magneticFieldData.length; i++) {
            double min = Double.MAX_VALUE;
            double max = Double.MIN_VALUE;
            for (int j = 0; j < magneticFieldData[i].length; j++) {
                if (magneticFieldData[i][j] < min) {
                    min = magneticFieldData[i][j];
                }
                if (magneticFieldData[i][j] > max) {
                    max = magneticFieldData[i][j];
                }
            }
            rangeOfMagneticFieldIntensities[i] = max - min;
        }

        // 3. Calculate the standard deviation of magnetic field intensities for each location
        double[] stdDevMagneticFieldIntensities = new double[magneticFieldData.length];
        for (int I = 0; I < magneticFieldData.length; i++) {
