export class AnimationAPI {
  public startAnimation(animation: Animation): void {
    this.camera.position = this.camera.position.add(animation.startPosition);
    this.camera.rotation = this.camera.rotation.multiply(animation.startRotation);
    this.camera.zoom = animation.startZoom;

    const positionDifference = animation.endPosition.subtract(animation.startPosition);
    const rotationDifference = animation.endRotation.multiply(this.camera.rotation.inverse());
    const zoomDifference = animation.endZoom - animation.startZoom;

    let position = this.camera.position;
    let rotation = this.camera.rotation;
    let zoom = this.camera.zoom;

    requestAnimationFrame(() => {
