function swapAdjectives(sentence) {
  const regex = /\b[a-zA-Z]+ly\b/g; // matches words ending with "ly"
  let match;

  while ((match = regex.exec(sentence)) !== null) {
    const [, adjective1, , adjective2] = match; // destructuring the match array

    const position1 = sentence.indexOf(adjective1);
