# Define function to check for SIDHistory
function CheckSidHistory {
    param(
        [Parameter(Mandatory=$true)]
        [string]$sid
    )

    $history = (Get-ADUser -Filter "samaccountname -eq '$($sid.Value(-1).SubString($sid.Value(-1).IndexOf("=")+1))'" -Properties sidhistory).SIDHistory
    foreach ($h in $history) {
        if (-not ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.SecurityIdentifier] $h)) {
            return $false
        }
    }
    return $true
}

# Function to get all folders and subfolders with ACLs
function GetFoldersWithACLs {
    param(
        [Parameter(Mandatory=$true)]
        [string]$path
    )

    Get-ChildItem -Path $path -Recurse -Force | Where-Object {$_.Mode -band [IO.FileMode]::Directory} | ForEach-Object {
        $acls = Get-Acl -Path $_.FullName
        $acls.Access | Where-Object {$_.IdentityReference -ne $_.IdentityReference.Value($acls.AccessControlType)} | ForEach-Object {
            $sid = New-Object Security.Principal.SecurityIdentifier($_.IdentityReference.Value($acls.AccessControlType))
            $history = CheckSidHistory -sid $sid
            Write-Host "Folder: $($_.FullName)"
            Write-Host "SID: $($sid)"
            Write-Host "History: $history"
            Write-Host ""
        }
        $acls | Set-Acl -Path $_.FullName
    }
}

# Main script
Get-FoldersWithACLs -path "C:\path\to\folder\"
