# Define function to check if a given SID is found in SIDHistory
function Get-SIDHistory {
    [CmdletBinding()]
    param(
        [Parameter(Mandatory=$true)]
        [string]$sid
    )

    $history = (Get-ADUser -Filter "SIDHistory*$sid*" | Select-Object -ExpandProperty SidHistory)

    return ($history -ne $null)
}

# Function to get all SIDs from an access control entry
function Get-ACLSID {
    [CmdletBinding()]
    param(
        [Parameter(Mandatory=$true)]
        [Security.AccessControl.FileSecurity] $acl
    )

    $sids = @()

    foreach($entry in $acl.GetAccessControl().Access) {
        $sids += $entry.IdentityReference.Value
    }

    return $sids
}

# Main function to recursively check folder permissions
function Check-FolderPermissions {
    [CmdletBinding()]
    param(
        [Parameter(Mandatory=$true)]
        [string]$folderPath
    )

    # Get folder ACL
    $acl = Get-Acl -Path $folderPath

    # Check each ACE
    foreach($ace in $acl.Access) {
        # Get all SIDs from this ACE
        $sids = Get-ACLSID -acl $acl

        # Check if any of these SIDs are found only in SIDHistory
