# Function to check if an SID is present in SIDHistory of a user or group
function CheckSidInSidHistory($sid) {
    $domain = (Get-ADDomain).Name
    $filter = "(&(objectCategory=group)(sid=$sid))"
    $groups = Get-ADGroup -Filter $filter | Select-Object -ExpandProperty SamAccountName

    foreach ($group in $groups) {
        $members = Get-ADGroupMember -Identity $group -Recursive | Select-Object -ExpandProperty SamAccountName
        foreach ($member in $members) {
            $sidhistory = (Get-ADUser -Filter {SamAccountName -eq "$member"} -Properties SidHistory).SidHistory
            if (-not [string]::IsNullOrEmpty($sidhistory)) {
                $sidhistoryarr = [System.Runtime.Interopservices.Marshal]::SecureStringToBSTR($sidhistory)
                $sidhistoriesegments = [System.Runtime.Interopservices.Marshal]::ReadIntPtr($sidhistoryarr) / 4
                for ($i = 0; $i -lt $sidhistoriesegments; $i++) {
                    $sidsegment = New-Object System.Security.Principal.NTAccount("$domain\$($sidhistoriesegments[$i])")
                    if ($sidsegment.Translate([System.Security.Principal.SecurityIdentifier])) {
                        if ($sidsegment.Value -eq $sid) {
                            return $true
                        }
                    }
                }
            }
        }
    }
    return $false
}

