class Transactions {
  String coinName;
  DateTime dateTime;
  double profitOrLoss;

  Transactions(this.coinName, this.dateTime, this.profitOrLoss);

  double get profitPercentage => profitOrLoss < 0 ? 0 : profitOrLoss / abs(profitOrLoss) * 100;
}

void main() {
