  var transactions = [
    Transactions('BTC', DateTime.now().subtract(Duration(days: 2)), 100),
    Transactions('ETH', DateTime.now().subtract(Duration(days: 1)), 50),
    Transactions('LTC', DateTime.now().subtract(Duration(days: 3)), 20),
    Transactions('BTC', DateTime.now().subtract(Duration(days: 4)), -100),
    Transactions('ETH', DateTime.now().subtract(Duration(days: 5)), -50),
    Transactions('LTC', DateTime.now().subtract(Duration(days: 6)), -20),
  ];

  var topProfitCoins = topNCoins(transactions, 3, (a, b) => a.profitPercentage.compareTo(b.profitPercentage));
  var topLossCoins = topNCoins(transactions, 3, (a, b) => b.profitPercentage.compareTo(a.profitPercentage));

  print('Top 3 coins with highest profit:');
  for (var coin in topProfitCoins) {
    print('${coin.coinName}, ${coin.profitPercentage.toStringAsFixed(2)}%');
  }

  print('Top 3 coins with highest loss:');
  for (var coin in topLossCoins) {
    print('${coin.coinName}, ${coin.profitPercentage.toStringAsFixed(2)}%');
  }
}

List<Transactions> topNCoins(List<Transactions> transactions, int n, Comparator<Transactions> comparator) {
  var sortedTransactions = transactions.toList()..sort(comparator);

  var topN = <Transactions>[];
  for (var I = 0; I < n && I < sortedTransactions.length; i++) {
    topN.add(sortedTransactions[i]);
  }

  return topN;
}
